<style lang="scss">
@import "../../scss/_global.scss";

#delivery {
  @extend %pbt-gray;

  .title {
    gap: 30px;

    @media (max-width: 1024px) {
      gap: 20px;
    }

    @media (max-width: 768px) {
      gap: 15px;
      .subtitle {
        width: calc(100% + 20px);
        transform: translateX(-10px);
      }
    }
  }

  .delivery-wrapper {
    display: grid;
    grid-template-columns: 1fr 2fr;

    gap: 30px;

    @media (max-width: 1440px) {
      gap: 20px;
    }

    @media (max-width: 1280px) {
      grid-template-columns: 1fr;
      gap: 40px;
    }

    @media (max-width: 425px) {
      gap: 18px;
    }

    &__form {
      max-width: 520px;
      width: 100%;
      margin: 0 auto;

      @media (max-width: 768px) {
        max-width: 425px;
      }
    }

    &__features {
      display: grid;
      grid-template-columns: repeat(3, 1fr);

      column-gap: 30px;

      @media (max-width: 1440px) {
        gap: 20px;
      }

      @media (max-width: 1280px) {
        max-width: max-content;
        margin: 0 auto;
      }

      @media (max-width: 768px) {
        display: none;
      }

      .features-item {
        display: flex;
        flex: 1 1 0;
        max-width: 330px;
        flex-direction: column;

        padding: 30px 20px 45px;

        @media (max-width: 1280px) {
          max-width: 260px;
          // gap: 25px;
        }

        @media (max-width: 992px) {
          padding: 24px 10px 45px;
        }

        &__svg {
          @media (max-width: 768px) {
            flex-shrink: 0;
          }
          svg {
            display: block;
            margin: 0 auto;
            margin-bottom: 30px;

            @media (max-width: 1280px) {
              margin-bottom: 25px;
            }
          }
        }

        &__content {
          h4 {
            font-size: 24px;
            font-family: "Raleway";
            font-weight: 600;
            line-height: 31px;
            text-align: center;
            color: #323232;
            margin-bottom: 15px;
            white-space: nowrap;

            br {
              display: none;
            }

            @include fluidFontSize(20, 24, 993, 1920);
          }

          p {
            font-size: 20px;
            font-family: "Raleway";
            line-height: 27px;
            text-align: center;
            font-weight: 400;
            color: #323232;
            white-space: nowrap;

            @include fluidFontSize(16, 20, 993, 1920);
          }
        }
      }
    }
  }
}
</style>
